// Landing.js
import './Landing.css';
import { useEffect, useState } from "react";
import Button from '@mui/material/Button';
import { useNavigate } from 'react-router-dom';

function Landing() {
    const CLIENT_ID = "90efe25a754f42f9840657669147d1b2"; // Replace with your Spotify Client ID
    const REDIRECT_URI = "http://localhost:3000/"; // Replace with your redirect URI
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
    const RESPONSE_TYPE = "token";

    const [token, setToken] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        // Extract token from URL hash if present
        const hash = window.location.hash;
        let storedToken = window.localStorage.getItem("token");

        if (!storedToken && hash) {
            storedToken = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1];

            // Clear the hash in the URL
            window.location.hash = "";
            window.localStorage.setItem("token", storedToken);
        }

        setToken(storedToken);

        // Navigate to home if authenticated
        if (storedToken) {
            navigate('/');
        }
    }, [navigate]);

    const handleLogin = () => {
        // Redirect to Spotify authorization endpoint
        window.location.href = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=user-top-read`;
    };

    return (
        <div className='Home'>
            <div className="App">
                <div className='titleArea'>
                    <div className='titleBox'>
                        <p className="title">TrackTheSpot</p>
                        <p className="subtitle">your personal Spotify data tracker</p>
                        <div className='button'>
                            <Button size="large" variant="contained" color="success" onClick={handleLogin}>
                                <p className='buttonText'>JOIN</p>
                            </Button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Landing;
