// Home.js
import './Home.css';
import { useEffect, useState } from "react";
import Table from './Table.js';
import HomeNavBar from './HomeNavBar.js';
import { useNavigate } from 'react-router-dom';

function Home() {
    const [token, setToken] = useState(window.localStorage.getItem("token") || "");
    const [topItems, setTopItems] = useState([]);
    const [activeDatatype, setActiveDatatype] = useState('tracks');
    const [activeDuration, setActiveDuration] = useState('short_term');
    const navigate = useNavigate();

    useEffect(() => {
        // Redirect to landing page if token is not available
        if (!token) {
            navigate('/landing');
            return;
        }

        fetchTopItems();
    }, [token, activeDatatype, activeDuration, navigate]);

    const fetchTopItems = async () => {
        try {
            const response = await fetch(
                `https://api.spotify.com/v1/me/top/${activeDatatype}?time_range=${activeDuration}&limit=50`,
                {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                }
            );

            if (!response.ok) {
                throw new Error('Failed to fetch top items');
            }

            const data = await response.json();
            setTopItems(data.items || []);
        } catch (error) {
            console.error("Error fetching top items:", error);
            // If there's an issue (e.g., token expired), clear the token and navigate to the landing page
            setToken("");
            window.localStorage.removeItem("token");
            navigate('/landing');
        }
    };

    const handleDatatypeClick = (datatype) => {
        setActiveDatatype(datatype);
    };

    const handleDurationClick = (duration) => {
        setActiveDuration(duration);
    };

    const logout = () => {
        setToken("");
        window.localStorage.removeItem("token");
        navigate('/landing');
    };

    return (
        <div className='Home'>
            <HomeNavBar />
            <div className='controls'>
                <button onClick={() => handleDatatypeClick('tracks')}>Songs</button>
                <button onClick={() => handleDatatypeClick('artists')}>Artists</button>
                <button onClick={() => handleDurationClick('short_term')}>Month</button>
                <button onClick={() => handleDurationClick('medium_term')}>6 Months</button>
                <button onClick={() => handleDurationClick('long_term')}>All Time</button>
                <button onClick={logout}>Logout</button>
            </div>
            <Table topItems={topItems} datatype={activeDatatype} />
        </div>
    );
}

export default Home;
