import './Table.css';
import Song from './Song.js';
import { useState, useEffect } from 'react';
import TableNavBar from './TableNavBar.js';

function Table() {

  const [activeDatatype, setActiveDatatype] = useState('tracks');
  const [activeDuration, setActiveDuration] = useState('short_term');
  const handleDurationClick = (duration) => {
    setActiveDuration(duration);
  };
  const handleDatatypeClick = (datatype) => {
    setActiveDatatype(datatype);
  };

  useEffect(() => {
    const getTopResults = async () => {
      const token = window.localStorage.getItem("token")
      var parameters = {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + token
        }
      }
      await fetch('https://api.spotify.com/v1/me/top/' + activeDatatype + '?time_range=' + activeDuration + '&limit=50', parameters)
        .then((response) => response.json())
        .then((data) => { console.log(data) });
    }
  }
  );



  return (
    <div className='table'>
      <div className='tableNavBar'>
        <link rel="stylesheet" href="https://use.typekit.net/lll4eml.css"></link>
        <div className='tableNavBar'>
          <div className='tableNavBarCategory'>
            <div className='tableNavBarItem' style={{ backgroundColor: activeDatatype === 'tracks' ? 'green' : 'red' }}
              onClick={() => handleDatatypeClick('tracks')}>Songs</div>
            <div className='tableNavBarItem' style={{ backgroundColor: activeDatatype === 'artists' ? 'green' : 'red' }}
              onClick={() => handleDatatypeClick('artists')}> Artists</div>
          </div>
          <div className='dash'>
            -
          </div>
          <div className='tableNavBarCategory'>
            <div className='tableNavBarItem' style={{ backgroundColor: activeDuration === 'short_term' ? 'green' : 'red' }}
              onClick={() => handleDurationClick('short_term')}>Month</div>
            <div className='tableNavBarItem' style={{ backgroundColor: activeDuration === 'medium_term' ? 'green' : 'red' }}
              onClick={() => handleDurationClick('medium_term')}>6 Months</div>
            <div className='tableNavBarItem' style={{ backgroundColor: activeDuration === 'long_term' ? 'green' : 'red' }}
              onClick={() => handleDurationClick('long_term')}>All Time</div>
          </div>
        </div>
      </div >
      <link rel="stylesheet" href="https://use.typekit.net/lll4eml.css"></link>
      <div className='songs'>
        <Song title="Let Down" artist="Radiohead" rank="1" link="https://open.spotify.com/track/2fuYa3Lx06QQJAm0MjztKr?si=2ff4b1986f964b60" image="https://i.scdn.co/image/ab67616d0000b273c8b444df094279e70d0ed856" />
      </div>
    </div>
  );
}

export default Table;
