// Home.js
import './Home.css';
import { useEffect, useState } from "react";
import Table from './Table.js';
import HomeNavBar from './HomeNavBar.js';
import { useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';

function Home({ onLogout }) {
    const [token, setToken] = useState(localStorage.getItem("token") || "");
    const [topItems, setTopItems] = useState([]);
    const [activeDatatype, setActiveDatatype] = useState('tracks');
    const [activeDuration, setActiveDuration] = useState('short_term');
    const navigate = useNavigate();

    useEffect(() => {
        // Redirect to landing page if token is not available
        if (!token) {
            navigate('/landing');
            return;
        }

        fetchTopItems();
    }, [token, activeDatatype, activeDuration, navigate]);

    const fetchTopItems = async () => {
        try {
            const response = await fetch(
                `https://api.spotify.com/v1/me/top/${activeDatatype}?time_range=${activeDuration}&limit=50`,
                {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                }
            );

            if (!response.ok) {
                throw new Error('Failed to fetch top items');
            }

            const data = await response.json();
            setTopItems(data.items || []);
        } catch (error) {
            console.error("Error fetching top items:", error);
        }
    };

    const handleDatatypeClick = (datatype) => {
        setActiveDatatype(datatype);
    };

    const handleDurationClick = (duration) => {
        setActiveDuration(duration);
    };

    return (
        <div className='Home'>
            <HomeNavBar onLogout={onLogout} />
            <div className='controls'>
                <div className='typeControls'>
                <Button variant="contained" size="large" onClick={() => handleDatatypeClick('tracks')}>Songs</Button>
                <Button variant="contained" size="large" onClick={() => handleDatatypeClick('artists')}>Artists</Button>
                </div>
                <div className='timeControls'>
                <Button variant="contained" size="large" onClick={() => handleDurationClick('short_term')}>Month</Button>
                <Button variant="contained" size="large" onClick={() => handleDurationClick('medium_term')}>6 Months</Button>
                <Button variant="contained" size="large" onClick={() => handleDurationClick('long_term')}>All Time</Button>
                </div>
            </div>
            <Table topItems={topItems} datatype={activeDatatype} />
        </div>
    );
}

export default Home;
