// Landing.js
import './Landing.css';
import { useEffect, useState } from "react";
import Button from '@mui/material//Button';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

function Landing() {
    const CLIENT_ID = "your_client_id_here";
    const REDIRECT_URI = "http://localhost:3000/";
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
    const RESPONSE_TYPE = "token";

    const [token, setToken] = useState("");
    const navigate = useNavigate(); // Use useNavigate for navigation

    useEffect(() => {
        const hash = window.location.hash;
        let token = window.localStorage.getItem("token");

        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1];
            window.location.hash = "";
            window.localStorage.setItem("token", token);
        }

        setToken(token);

        if (token) {
            navigate('/'); // Navigate to Home if token is present
        }
    }, [navigate]);

    return (
        <div className='Home'>
            <div className="App">
                <link rel="stylesheet" href="https://use.typekit.net/lll4eml.css"></link>
                <div className='titleArea'>
                    <div className='titleBox'>
                        <p className="title">TrackTheSpot</p>
                        <p className="subtitle">your personal Spotify data tracker</p>
                        <div className='button'>
                            <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>
                                <Button size="large" variant="contained" color="success">
                                    <p className='buttonText'>JOIN</p>
                                </Button>
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Landing;
