import './App.css';
import { useEffect, useState, useRef } from "react";
import Home from './Home.js';
import Landing from './Landing.js';
import Table from './Table.js';
import AboutPage from './AboutPage.js';
import RecPage from './RecPage.js';

import { BrowserRouter, Route, Routes } from 'react-router-dom'

function App() {
  const CLIENT_ID = "90efe25a754f42f9840657669147d1b2"
  const CLIENT_SECRET = "160151fd4819490cbab9ecabceaf1800"


  const [token, setToken] = useState("")



  useEffect(() => {
    var authParameters = {
      method: "POST",
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body:'grant_type=client_credentials&client_id='+CLIENT_ID+'&client_secret='+CLIENT_SECRET
    }

    fetch('https://accounts.spotify.com/api/token',authParameters)
    .then(result => result.json())
    .then(data => {
      setToken(data.access_token)
      window.localStorage.setItem("token",token)
    })

  },[])

  const logout = () => {
    setToken("")
    window.localStorage.removeItem("token")
  }
  return (
    <div className='main'>


      <BrowserRouter>
        <Routes>
        <Route path="/recommendations" element={!token ? <Landing onClick={logout} /> : <div>
        <RecPage />
          </div>} />
          <Route path="/about" element={!token ? <Landing onClick={logout} /> : <div>
            <AboutPage />
          </div>} />

          <Route path="/" element={!token ? <Landing onClick={logout} /> : <div><Home /></div>}>
            <Route path="/songs">
              <Route path="/songs/alltime" />
              <Route path="/songs/month" />
              <Route path="/songs/6months" />
            </Route>

            <Route path="/artists">
              <Route path="/artists/alltime" />
              <Route path="/artists/month" />
              <Route path="/artists/6months" />
            </Route>

          </Route>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
